package javaPractice;

public class A6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// while and do-while loop
		int a=10;
		int b=0;
		while ( a >=0)
			
		{
			
			System.out.println(a);
			a--;
		}
		System.out.println("==========================================================");
		do {
			
			System.out.println(b);
			b++;
		}while (b<5);

	}

}
